  }

  render(){
  return (
    <div className="App">
      <h1>BEST OFFER OR ELSE</h1>
      <Switch>
        <Route path='/profile' render={ () => <Profile users={this.state.allUsers}/>} />
        <Route path='/login' render={ () => <Login login={this.currentUser}/>}/>
        <Route path='/' render={ () => <Redirect to="/login" />}/>
      </Switch>
    </div>
  );
  }
}
