import React from 'react'
import { Button, Card, Image } from 'semantic-ui-react'

export default class ProfileContainer extends React.Component {
  sortBids = () => {
    let sorted = this.props.task.bids.sort((a, b) =>
      a.price > b.price ? 1 : -1
    );
    return sorted;
  };


CardExampleGroups = (bid) => (
