import React from "react";
import ContractorShowPage from "../components/ContractorShowPage"
import { Card } from "semantic-ui-react";
import {
  // eslint-disable-next-line
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from "react-router-dom";



const URL = 'http://localhost:3000/contractors'

export default class ContractorsContainer extends React.Component {

    state = {
        allContractors: []
    }


    componentDidMount(){
      fetch(URL)
      .then(res => res.json())
      .then(allContractors => this.setState({allContractors}))
    }


    cardExampleLinkCard = contractor => (
      <Card
        color="blue"
        href={`/contractors/${contractor.id}`}
        header={contractor.name}
        // meta={task.specialty.name[0].toUpperCase() + task.specialty.name.slice(1)}
        description={`Description: ${task.description} ${this.sortBids(task)}`}
      />
    );

    render() {
      console.log(this.props)
      console.log(this.state.allContractors)

        return (
          <div >
              Contractor Container


              {this.state.allContractors.map(contractor => {
                this.cardExampleLinkCard(contractor)
              }
              )}



              <Route path={`${this.props.match.url}/:id`} component={ContractorShowPage}/>

          </div>
        );
      }


}
