import React from "react";
import "./App.css";
import "./App.sass";
import Login from "./components/Login";
import Profile from "./containers/ProfileContainer";
import ReactDOM from "react-dom";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from "react-router-dom";

const URL = "http://localhost:3000/users/";

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      currentUser: null
    };
  }

  // componentDidMount(){
  //   fetch(URL)
  //   .then(res => res.json())
  //   .then(allUsers => this.setState({
  //     allUsers
  //   }))
  // }

  logout = () =>{
    console.log('click')
    this.state = {
      currentUser: null
    }
  }

  currentUser = event => {
    event.preventDefault()
    fetch(URL + event.target.name.value, {})
      .then(res => res.json())
      .then(currentUser => {
        this.setState({
          currentUser: currentUser
        });
      });
  };

  render() {
    console.log(this.state.currentUser)
    return (
      <div className="App">
        <h1>BEST OFFER OR ELSE</h1>
        <button onClick={this.logout}>Logout</button>
        <Switch>

            <Route exact path='/login' render={ () => this.state.currentUser
            ? 
            <Redirect to="/profile" />
            :
            <Login login={this.currentUser} />
            }
            />

            <Route exact path='/profile' render={ () => this.state.currentUser
            ? <Profile user={this.state.currentUser}/> :
            <Redirect to="/login" /> }/>



        </Switch>
      </div>
    );
  }
}
