import React from "react";
import { Button, Comment, Form, Header } from "semantic-ui-react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from "react-router-dom";

export default class ContractorShowPage extends React.Component {
  state = {};

  CommentExampleComment = (review) => {
      console.log(review)

    return <Comment>
      <Comment.Avatar src="/images/avatar/small/matt.jpg" />
      <Comment.Content>
        <Comment.Author as="a">{review.user.name}</Comment.Author>
        <Comment.Metadata>
          <div>{review.created_at.split('T')[0]}</div>
        </Comment.Metadata>
        <Comment.Text>{review.content}</Comment.Text>
        {/* <Comment.Actions>
          <Comment.Action>Reply</Comment.Action>
        </Comment.Actions> */}
      </Comment.Content>
    </Comment>
  };

  render() {
    console.log(this.props.contractor)
    return (
      <div >

        <Comment.Group>
          <Header as="h3" dividing>
            Reviews
          </Header>
          {this.props.contractor.reviews.map(review => this.CommentExampleComment(review))}
          -----------------------------------------
          <Form reply>
            <Form.TextArea />
            <Button
              content="Add Reply"
              labelPosition="left"
              icon="edit"
              primary
            />
          </Form>
        </Comment.Group>
      </div>
    );
  }
}
