import React from "react";
import ContractorShowPage from "../components/ContractorShowPage"



const URL = 'http://localhost:3000/contractors'

export default class ContractorsContainer extends React.Component {

    state = {
        allContractors: null
    }


    componentDidMount(){
      fetch(URL)
      .then(res => res.json())
      .then(allContractors => this.setState({allContractors}))
    }


    render() {

        return (
          <div >

              <Route exact 
                path="/contractors/:id"
                render={(props) =>{  
                 let taskObj =  this.state.currentUser.tasks.find(task => task.id == props.match.params.id) 
                  return   !isEmpty(taskObj) ?
                  <TaskShowPage

                  />
                  :
                  <Redirect to="/profile" />

                }}/>


          </div>
        );
      }


}
