import Profile from "../components/profile";
import TaskShowPage from "../components/TaskShowPage";
import {Link} from 'react-router-dom'
import {
  // eslint-disable-next-line
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from "react-router-dom";

