      task: null
    }

    togg = () =>{
      thie.setState
    }

    togglePage = (task) =>{

      this.setState({
       task: task,
        profile: this.state.profile ? false : true
      })

    }

  render() {
    return (
      <div className="columns">
        <div className="column" style={{ justifyContent: "flex-end" }}>
          <Switch>

          {this.state.profile 
          ? 
        <Profile
            user={this.props.user}
            showTaskPage={this.togglePage}
            />
            :
            <TaskShowPage
              user={this.props.user}
              task={this.state.task}
              togglePage={this.togglePage}
            />

          }
          </Switch>
        </div>
      </div>
    );
  }
}

