  averageStars = () => {
    let sum = this.props.contractor.reviews.reduce((a, b) => ({
      stars: a.stars + b.stars
    })).stars;

    let avg = sum / this.props.contractor.reviews.length;
    return avg.toFixed(2);
  };

  render() {
    return (
      <div>
        <br />
        <br />
        <br />
        <div className="bids">
          <Comment.Group>
            <Header as="h3" dividing>
              Reviews
              <p> (Average Rating: {this.averageStars()}) </p>
            </Header>
            <Form
              reply
              onSubmit={event =>
                this.props.addNewReview(
                  event,
                  this.state.rating,
                  this.props.contractor
                )
              }
            >
              <Rating
                disabled={this.props.disableForm}
                icon="star"
                name="rating"
                defaultRating={Math.floor(this.averageStars())}
                maxRating={5}
                onRate={this.handleRate}
                size="massive"
              />
              <Form.TextArea name="content" disabled={this.props.disableForm}  />
              <Button
                disabled={this.props.disableForm}
                content="Leave Review"
                labelPosition="left"
                icon="edit"
                primary
              />
            </Form>
          </Comment.Group>
        </div>
      </div>
    );
  }
}
