import React from "react";
import NewTaskForm from "./NewTaskForm";
import { isEmpty } from "lodash";
import {Link} from 'react-router-dom'
import { Card, Feed } from 'semantic-ui-react'
// import store from '../redux/store'
// import {Link} from 'react-router-dom'

const SPECIALTIES = "http://localhost:3000/specialties/";
const TASKS = "http://localhost:3000/tasks/";

export default class extends React.Component {
  state = {
    specialties: [{id: 1, name: "gardening"},
     {id: 2, name: "capentry"},
     {id: 3, name: "plumbing"},
     {id: 4, name: "technology"},
     {id: 5, name: "electrician"} ],
    currentUser: {}
  };

  componentDidMount() {
    fetch(SPECIALTIES)
      .then(res => res.json())
      .then(specialties =>{

        this.setState({
          currentUser: this.props.user
        })

      });
  }

  addTask = event => {
    event.preventDefault();
    let data = {
      name: event.target.name.value,
      description: event.target.description.value,
      specialty_id: event.target.specialty.value,
      user_id: this.props.user.id
    };
    event.target.reset();
    console.log(event);
    fetch(TASKS, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data)
    })
      .then(res => res.json())
      .then(currentUser =>
        this.setState({
          currentUser
        })
      );
  };

  sortBids = task => {
    let sorted = task.bids.sort((a, b) => (a.price > b.price ? 1 : -1));

      console.log()
    return sorted[0] ? `${sorted[0].price} by ${sorted[0].contractor.name}` : '  No Bids Yet'
  };

  CardExampleLinkCard = (task) => (
    <Card

      href={`/task/${task.id}`}
      header={task.name}
      meta='Friend'
      description={task.description}
    />
  )
rederForm = () => {
  <Form>
  <Form.Group widths='equal'>
    <Form.Input fluid label='First name' placeholder='First name' />
    <Form.Input fluid label='Last name' placeholder='Last name' />
    <Form.Select
      fluid
      label='Gender'
      options={options}
      placeholder='Gender'
    />
  </Form.Group>
  <Form.Group inline>
    <label>Size</label>
    <Form.Radio
      label='Small'
      value='sm'
      checked={value === 'sm'}
      onChange={this.handleChange}
    />
    <Form.Radio
      label='Medium'
      value='md'
      checked={value === 'md'}
      onChange={this.handleChange}
    />
    <Form.Radio
      label='Large'
      value='lg'
      checked={value === 'lg'}
      onChange={this.handleChange}
    />
  </Form.Group>
  <Form.TextArea label='About' placeholder='Tell us more about you...' />
  <Form.Checkbox label='I agree to the Terms and Conditions' />
  <Form.Button>Submit</Form.Button>
</Form>
}
  render() {

    return (
      <div >
        <div>
          Hi, {this.state.currentUser ? this.state.currentUser.name : null}
        </div>
        <NewTaskForm
          specialties={this.state.specialties}
          addTask={this.addTask}
        />
        <div >
          Current Tasks{" "}
          <br /><br />
          {!isEmpty(this.state.currentUser)
            ? 
              this.state.currentUser.tasks.map(task => { 
              return  <div  key={Math.floor((Math.random() * 100000000000) + 1)} onClick={() => this.props.currentTask(task)}>
         { this.CardExampleLinkCard(task)}
              </div>})

            : null}
        </div>
      </div>
    );
  }
}
