import React from "react";
import "./App.css";
import "./App.sass";
import Login from "./components/Login";
import Profile from "./containers/ProfileContainer";
import ReactDOM from "react-dom";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from "react-router-dom";

const URL = "http://localhost:3000/users/";

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      currentUser: null
    };
  }

  // componentDidMount(){
  //   fetch(URL)
  //   .then(res => res.json())
  //   .then(allUsers => this.setState({
  //     allUsers
  //   }))
  // }

  logout = () =>{
    console.log('click')
    this.state = {
      currentUser: null
    }
  }

  currentUser = event => {

    fetch(URL + event.target.name.value, {})
      .then(res => res.json())
      .then(currentUser => {
        debugger;
        this.setState({
          currentUser: currentUser
        });
      });
  };

  render() {
    console.log(this.state.currentUser)
    return (
      <div className="App">
        <h1>BEST OFFER OR ELSE</h1>
        <button onClick={this.logout}>Logout</button>
        <Switch>

          {/* {
          <Route exact path="/profile" render={() => this.state.currentUser ? 
            <Route exact path="/login"
            render={() => <Login login={this.currentUser} />}
          />:
          <Profile currentUser={this.state.currentUser}/> 

       } />

        } */}
        {this.state.currentUser ? <Route exact path="/profile" render={() => <Profile currentUser={this.state.currentUser}/> }/>

          :
         <Route exact path="/login"
            render={() => <Login login={this.currentUser} />}
          />
        }
        <Route path="/" render={() => <Redirect to="/login" />} />


        </Switch>
      </div>
    );
  }
}
