import React from "react";
import { Button, Card, Image } from "semantic-ui-react";
import { Link } from "react-router-dom";

const TASKS = "http://localhost:3000/tasks/"

export default class ContractorTaskShowPage extends React.Component {
  state = {
    anyBidsSelected: false,
    sortedBids: [],
    currentTask: []
  };

  sortBids = (bids) => {
    debugger
    let sorted = bids.sort((a, b) =>
      a.price > b.price ? 1 : -1
    );
    return sorted;
  };

  bidAccepted = bid => {
    this.setState(
      {
        anyBidsSelected: true
      },
      () => this.props.acceptBid(bid, this.props.task)
    );
  };

  bidDeclined = bid => {
    if (bid.status) {
      this.setState(
        {
          anyBidsSelected: false
        },
        () => this.props.deleteBid(bid)
      );
    } else {
      this.props.deleteBid(bid);
    }
  };

  componentDidMount = () => {

    fetch(TASKS + this.props.id)
    .then(res => res.json())
    .then(task => {
      console.log(task)
      this.setState({
        currentTask: task
      })
      task.bids.forEach(bid => {
      if (bid.status) {
        this.setState({
          anyBidsSelected: true
        });
        return true;
      }
    });

    this.setState({
      sortedBids: this.sortBids(task.bids)
    })
  });
  };

  CardExampleGroups = (bid, index) => {
    debugger
    return (
      <Card.Group>
        <Card>
          <Card.Content href={`/contractors/${bid.contractor.id}`}>
            <Image floated="right" size="mini" src={bid.contractor.img_url} />
            <h1>
              {index === 0 && !this.state.anyBidsSelected
                ? "Best Value!"
                : null}
            </h1>
            {bid.status ? <h1>Congrats!!!!! You selected this bid!</h1> : null}
            <Card.Header>{bid.contractor.name}</Card.Header>
            <Card.Meta>
              {bid.status ? `Winning Bid: $${bid.price}` : "Top Contractor!"}
            </Card.Meta>
            <Card.Description>{bid.contractor.name} Pick Me!</Card.Description>
          </Card.Content>
          <Card.Content extra>
            {!bid.status ? (
              <div className="ui two buttons">
                <Button
                  disabled={this.state.anyBidsSelected}
                  onClick={() => this.bidAccepted(bid)}
                  basic
                  color="blue"
                >
                  Accept bid of ${bid.price}
                </Button>
                <Button
                  disabled={bid.status ? true : null}
                  onClick={() => this.bidDeclined(bid)}
                  basic
                  color="red"
                >
                  {this.state.anyBidsSelected ? "Delete Bid" : "Decline Bid"}
                </Button>
              </div>
            ) : null}
          </Card.Content>
        </Card>
      </Card.Group>
    );
  };

  render() {
    return (
      <div>
        <Link to="/profile">
          {" "}
        </Link>
           <h1>
          Title:{" "}
          <h3>
            <strong>{this.state.currentTask.name}</strong>
          </h3>{" "}
        </h1>
        <h1>
          Description: <h3>{this.state.currentTask.description}</h3>{" "}
        </h1>

        <h1>Bids:</h1>
        <div className="bids">
          {this.state.sortedBids.map((bid, index) => (
            <div key={Math.floor(Math.random() * 100000000000 + 1)}>
              {" "}
              {this.CardExampleGroups(bid, index)}
            </div>
          ))}
        </div>
      </div>
    );
  }
}
